from basics import inner_dotplot


def test_result_of_inner_dotplot():
	assert inner_dotplot('AB', 'AB', code=True) == ['A', '-', '|A\n', '-', 'B', '|B\n']


def test_result_of_inner_dotplot2():
	assert inner_dotplot('ABCDEF', 'ABCDEF', code=True) == ['A', '-', '-', '-', '-', '-', '|A\n',
															 '-', 'B', '-', '-', '-', '-', '|B\n',
															 '-', '-', 'C', '-', '-', '-', '|C\n',
															 '-', '-', '-', 'D', '-', '-', '|D\n',
															 '-', '-', '-', '-', 'E', '-', '|E\n',
															 '-', '-', '-', '-', '-', 'F', '|F\n']


